// Function to remove the message box on click
function removeMessageBox() {
    var messageBox = document.querySelector('.message_box');
    if (messageBox) {
      messageBox.remove();
    }
  }
  
  // Wait for the DOM to be fully loaded
  document.addEventListener('DOMContentLoaded', function() {
    // Attach click event listener to message box
    var messageBox = document.querySelector('.message_box');
    if (messageBox) {
      messageBox.addEventListener('click', removeMessageBox);
    }
  });
  